package com.kita.first.level1;

public class Operator3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1=3;
		int n2=2;
		System.out.println(n1+n2);
		System.out.println(n1-n2);
		System.out.println(n1*n2);
		System.out.println(n1/n2); //연산의 몫
		System.out.println(n1%n2);
		
		double result1=(double)n1/n2;
		System.out.println(n1%n2);
	}

}
