package com.kita.first.level1;

public class Operator4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=7;
		n+=4;
		System.out.println(n);
		n-=4;
		System.out.println(n);
		n*=4;
		System.out.println(n);
		n/=4;
		System.out.println(n);
		n%=4;
		System.out.println(n);
	}

}
